mapper.select:
select wh_id, wh_name, wh_code, wh_type, wh_status, customer_id, customer_name, principal,
mobile, mail, tel_num, province, city, county, detail_address, post_code, remark,
create_id, create_name, create_date, update_id, update_name, update_time, is_deleted,
company_id
from t_warehouse

  company_id = #{companyId,jdbcType=BIGINT}

    and  is_deleted = #{isDeleted,jdbcType=SMALLINT}


    and  wh_type = #{whType,jdbcType=SMALLINT}


    and  wh_status = #{whStatus,jdbcType=SMALLINT}


order by wh_id desc
